import React, { useEffect, useState } from 'react'
import ListCard from './ListCard'
import './content.css'
import { DBdummy } from '../Dummy'
import CloseBtn from '../assets/delete_icon.svg'
import FormEdit from './FormEdit'

const Content = () => {

  const [dbCopy, setDbCopy] = useState([]);
  const [editDB, setEditDB] = useState([]);
  const [editStatus, setEditStatus] = useState(false)
  const [editTodo, setEditTodo] = useState('')
  const [editDate, setEditDate] = useState('')
  const [editPriority, setEditPriority] = useState(true)

  useEffect(() => {
    setDbCopy(DBdummy)
  }, [])

  const delHandle = (id) => {
    const delDB = dbCopy.filter(item => item._id !== id)
    setDbCopy(delDB)
  }

  const editHandle = (id) => {
    setEditStatus(true)
    const getDB = dbCopy.find(item => item._id === id);
    console.log(getDB);
    setEditTodo(getDB.todo)
    // getDB => edit form => onChange to editDB => onSubmit setEditDB._id === editDB
  }

  const handleSubmit = () => {
    console.log('works');
  }

  return (

    <div className="contentWrapper">
      {
        dbCopy.map((item) => {
          return (
            <ListCard
              key={item._id}
              date={item.date}
              todo={item.todo}
              priority={item.priority}
              delHandle={() => delHandle(item._id)}
              editHandle={() => editHandle(item._id)}
            />
          )
        })
      }
      {
        editStatus ?
          <div className="editContainer">
            <div className='editModal'>
              <div className='closeBtn' onClick={() => setEditStatus(false)}>
                <img src={CloseBtn} alt="" />
              </div>
              <div>
                <div className='headerEdit'>edit to do</div>
                <div className='editF'>
                  <form className='ltdForm editLtd'>
                    <div className='iForm'>
                      <label htmlFor='date'>Date</label>
                      <input type='text' id='date' name="date" value={editDate} onChange={(e) => setEditDate(e.target.value)} />
                    </div>
                    <div className='iForm'>
                      <label htmlFor='todo'>To Do</label>
                      <textarea id='todo' name="todo" rows="5" cols="50" value={editTodo} onChange={(e) => setEditTodo(e.target.value)} />
                    </div>
                    <div className='iForm' onChange={(e) => editPriority(e.target.value)}>
                      <label htmlFor='todo'>Priority</label>
                      <div className='radioF'>
                        <label className='iRadio'>
                          <input type="radio" id='priority' name="priority" value={true} />
                          Yes
                        </label>
                        <label className='iRadio'>
                          <input type="radio" id='priority' name="priority" value={false} />
                          No
                        </label>
                      </div>
                    </div>
                    <button onClick={handleSubmit} className="submitBtn">Submit</button>
                  </form>
                </div>
              </div>
            </div>
          </div>
          : null
      }

    </div>

  )
}

export default Content
