import React, { useState } from 'react'
import { createContext } from 'react'


export const DBTodoContext = createContext()



const DBContextProvider = ({ children }) => {

    const [DBTodo, setDBTodo] = useState([])

    function getTodo(id) {
        const getDB = dbCopy.find(item => item._id === id);
        setDBTodo(getDB);
    }

    return (
        <DBTodoContext.Provider value={{ DBTodo: DBTodo, getTodo: getTodo }}>
            {children}
        </DBTodoContext.Provider>
    )
}

export default DBContextProvider
